header {
    position: fixed;
    width: 100%;
    height: 48px;
    background-color: black;
    top: 0;
    left: 0;
}

.burger-menu {
    width: 24px;
    height: 28px;
    position: relative;
    top: 50%;
    transform: translateY(-50%);
    margin-left: 18px;
    cursor: pointer;

    & span {
        width: 100%;
        height: 3px;
        background-color: white;
        display: block;
        position: relative;
        top: 50%;
        transform: translateY(-50%);
        transition: background-color 0.3s ease-in-out;

        &::before, &::after {
            content: "";
            position: absolute;
            width: 100%;
            height: 100%;
            background-color: white;
            transition: transform 0.3s ease-in-out;
        }

        &::before {
            transform: translateY(-8px);
        }

        &::after {
            transform: translateY(8px);
        }
    }

    &.isOpen span {
        background-color: transparent;

        &::before {
            transform: translateY(0px) rotate(45deg);
        }

        &::after {
            transform: translateY(0px) rotate(-45deg);
        }
    }
}

