.burger-menu {
    width: 24px;
    height: 28px;
    position: relative;
    top: 50%;
    transform: translateY(-50%);
    margin-left: 14px;
    cursor: pointer;

    &:hover span {
        background-color: gray;

        &::before, &::after {
            background-color: gray;
        }
    }

    & span {
        width: 100%;
        height: 2px;
        background-color: white;
        display: block;
        position: relative;
        top: 50%;
        transform: translateY(-50%);

        &::before, &::after {
            content: "";
            position: absolute;
            width: 100%;
            height: 100%;
            background-color: white;
            transition: transform 0.3s ease-in-out;

            span:hover & {
                background-color: gray;
            }
        }

        &::before {
            transform: translateY(-8px);
        }

        &::after {
            transform: translateY(8px);
        }
    }

    &.isOpen span {
        background-color: transparent;

        &::before {
            transform: translateY(0px) rotate(45deg);
        }

        &::after {
            transform: translateY(0px) rotate(-45deg);
        }
    }
}

.header-logo {
    height: 28px;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    cursor: pointer;
    fill: white;
    
    a:hover & {
        fill: gray;
    }
}

.login-icon, .cart-icon {
    height: 24px;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    right: 0;
    margin-right: 18px;
    fill: white;
    cursor: pointer;

    &:hover {
        fill: gray;
    }
}

.login-icon {
    margin-right: 54px;
}
