        &::before, &::after {
            content: "";
            position: absolute;
            width: 100%;
            height: 100%;
            background-color: black;
            transition: transform 0.3s ease-in-out;

            span:hover & {
                background-color: gray;
            }
        }

        &::before {
            transform: translateY(-8px);
        }

        &::after {
            transform: translateY(8px);
        }
